import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { FaUserCircle } from 'react-icons/fa'; // User icon
import './Home.css';

const Home = () => {
  const [user, setUser] = useState(null);
  const [showUserMenu, setShowUserMenu] = useState(false); // Toggle user menu
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserDetails = async () => {
      const token = localStorage.getItem('token');
      if (!token) return;

      try {
        const response = await axios.get('http://localhost:5000/api/auth/user-profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(response.data);
      } catch (err) {
        console.error('Error fetching user details:', err);
      }
    };

    fetchUserDetails();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div className="home-page">
      <header className="hero-section">
        <h1>Welcome to HackTrek</h1>
        <p>
          Dive into the world of web security by tackling real-world vulnerabilities. 
          HackTrek is your ultimate platform for learning ethical hacking and testing your skills!
        </p>
      </header>

      <section className="about">
        <h2>About HackTrek</h2>
        <div className="about-container">
          <div className="about-text">
            <p>
              HackTrek is an educational platform designed to teach web security concepts through practical challenges. 
              Whether you're a beginner or an expert, our gamified approach will help you hone your hacking skills 
              while ensuring a fun and engaging experience.
            </p>
            <ul>
              <li>Learn to identify and exploit security flaws.</li>
              <li>Master web vulnerabilities like NoSQL Injection and XSS.</li>
              <li>Gain hands-on experience with real-world scenarios.</li>
              <li>Complete the Challenges and earn rewards for your skills.</li>
            </ul>
          </div>
          <div className="about-image">
          </div>
        </div>
      </section>

      <section className="features">
        <h2>What Youâ€™ll Experience</h2>
        <div className="feature-list">
          <div className="feature">
            <img src="data:image/jpeg;base64," alt="Challenges" />
            <h3>Exciting Challenges</h3>
            <p>
              Solve security vulnerabilities across categories like SQL Injection, XSS, CSRF, and more.
            </p>
          </div>
          <div className="feature">
            <img src="data:image/jpeg;base64," alt="Learning" />
            <h3>Hands-On Learning</h3>
            <p>
              Gain practical experience with real-world web application vulnerabilities.
            </p>
          </div>
          <div className="feature">
            <img src="data:image/jpeg;base64," alt="Leaderboard" />
            <h3>Gamified Experience</h3>
            <p>
              Compete with others, climb the leaderboard, and showcase your skills.
            </p>
          </div>
          <div className="feature">
            <img src="data:image/jpeg;base64," alt="Community" />
            <h3>Supportive Community</h3>
            <p>
              Join a community of like-minded individuals passionate about web security.
            </p>
          </div>
        </div>
      </section>


      {/* User Icon */}
      <div className="user-icon-container">
        <FaUserCircle size={40} onClick={() => setShowUserMenu(!showUserMenu)} />
        {showUserMenu && user && (
          <div className="user-menu">
            <p><strong>Username:</strong> {user.username}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <button onClick={handleLogout}>Logout</button>
          </div>
        )}
      </div>

      <footer className="footer">
        <p>Want to learn more? Reach out at: <strong>{user ? user.email : 'support@hacktrek.com'}</strong></p>
      </footer>
    </div>
  );
};

export default Home;
